import React from "react";

const Messages = ({ messages, currentMember, isTyping }) => {
  const renderMessage = (message) => {
    const { id, member, text, timestamp } = message;
    const messageFromMe =
      member && member.clientData && member.clientData.username === currentMember.username;
    const className = messageFromMe
      ? "Messages-message currentMember"
      : "Messages-message";

    const avatarStyle = {
      backgroundColor: member?.clientData?.color ?? "#000000",
    };

    const username = member?.clientData?.username ?? "Unknown User";

    const formattedTimestamp = formatTimestamp(timestamp);



    return (
      <li key={id} className={className}>
        <span className="avatar" style={avatarStyle} />
        <div className="Message-content">
          <div className="username">{username}</div>
          <div className="text">{text}</div>
          <div className="timestamp">{formattedTimestamp}</div>
        </div>
      </li>
    );
  };

  return <ul key={messages.length} className="Messages-list">{messages.map(renderMessage)}
  {isTyping && (
        <li className="Messages-message">
          <div className="typing-indicator">Typing...</div>
        </li>
      )}
      </ul>;
};

const formatTimestamp = (timestamp) => {
  const date = new Date(timestamp);
  const options = {
    hour: "numeric",
    minute: "numeric",
  };
  const formattedTime = date.toLocaleTimeString([], options);
  return `${formattedTime}`;
};

export default Messages;
