import React, { useEffect, useRef } from 'react';
import './App.css';

const Messages = ({ messages, currentMember }) => {
	const messagesRef = useRef(null);

	useEffect(() => {
		scrollToBottom();
	}, [messages]);

	const scrollToBottom = () => {
		if (messagesRef.current) {
			messagesRef.current.scrollTop = messagesRef.current.scrollHeight;
		}
	};
	const renderMessage = (message) => {
		const { member, text, timestamp } = message;
		const messageFromMe =
			member &&
			member.clientData &&
			member.clientData.username === currentMember.username;
		const className = messageFromMe
			? 'Messages-message currentMember'
			: 'Messages-message';

		const avatarStyle = {
			backgroundColor: member?.clientData?.color ?? '#000000',
		};

		const username = member?.clientData?.username ?? 'Unknown User';

		const formattedTimestamp = formatTimestamp(timestamp);

		return (
			<li key={message.id} className={className}>
				<span className="avatar" style={avatarStyle} />
				<div className="Message-content">
					<div className="username">{username}</div>
					<div className="text">{text}</div>
					<div className="timestamp">{formattedTimestamp}</div>
				</div>
			</li>
		);
	};

	return (
		<ul key={message.id} ref={messagesRef} className="Messages-list">
			{messages.map(renderMessage)}
		</ul>
	);
};

const formatTimestamp = (timestamp) => {
	const date = new Date(timestamp);
	const options = {
		hour: 'numeric',
		minute: 'numeric',
	};
	const formattedTime = date.toLocaleTimeString([], options);
	return `${formattedTime}`;
};

export default Messages;
