import React from "react";

const Messages = ({ messages, currentMember }) => {
  const renderMessage = (message) => {
    const { member, text } = message;
    const messageFromMe = member && member.id === currentMember.id;
    const className = messageFromMe
      ? "Messages-message currentMember"
      : "Messages-message";

    const avatarStyle = member && member.clientData && member.clientData.color
      ? { backgroundColor: member.clientData.color }
      : {};

    const username = member && member.clientData && member.clientData.username;

    return (
      <li className={className}>
        <span className="avatar" style={avatarStyle} />
        <div className="Message-content">
          <div className="username">{username}</div>
          <div className="text">{text}</div>
        </div>
      </li>
    );
  };

  return (
    <ul className="Messages-list">
      {messages.map((m) => renderMessage(m))}
    </ul>
  );
};

export default Messages;
