import React, { useState, useEffect, useMemo } from 'react';
import './App.css';
import Messages from './Messages';
import { randomColor, randomName } from './Random';
import Input from './Input';
import 'remixicon/fonts/remixicon.css';

const App = () => {
  const [messages, setMessages] = useState([]);
  const [member, setMember] = useState({
    username: randomName(),
    color: randomColor(),
  });
  const [onlineUsers, setOnlineUsers] = useState([]);

  const drone = useMemo(() => {
    return new window.Scaledrone('23axSpJJy7sehPRM', {
      data: member,
    });
  }, []);

  useEffect(() => {
    drone.on('open', (error) => {
      if (error) {
        console.error(error);
        return;
      }
      const updatedMember = { ...member };
      updatedMember.id = drone.clientId;
      setMember(updatedMember);
    });

    const room = drone.subscribe('observable-room');

    room.on('open', (error) => {
      if (error) {
        console.error(error);
        return;
      }
      console.log('Connected to room');
    });

    room.on('members', (members) => {
      setOnlineUsers(members);
    });

    room.on('member_join', (member) => {
      setOnlineUsers((prevUsers) => {
        // Check if the member is already present in the onlineUsers array
        if (prevUsers.some((user) => user.id === member.id)) {
          return prevUsers;
        }
        return [...prevUsers, member];
      });
    });

    room.on('member_leave', (member) => {
      setOnlineUsers((prevUsers) =>
        prevUsers.filter((user) => user.id !== member.id)
      );
    });

    room.on('data', (data, member) => {
      setMessages((prevMessages) => [
        ...prevMessages,
        {
          text: data,
          member: member,
          timestamp: Date.now(),
        },
      ]);
    });
  }, [drone, member]);

  const onSendMessage = (message) => {
    if (message.trim() !== '') {
      drone.publish({
        room: 'observable-room',
        message,
      });
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        C H A T A O N I C A
        <div className="Online">
          <div className="Wellcome">
            Welcome! Today you are chatting with these cool people: (yes, you are on the list too... ðŸ™„)
          </div>
          <div className="Users">
            {onlineUsers.map((user) => user.clientData.username).join(', ')}
          </div>
        </div>
      </header>
      <Messages messages={messages} currentMember={member} />
      <Input onSendMessage={onSendMessage} />
    </div>
  );
};

export default App;
