import React from "react";

const Messages = ({ messages, currentMember }) => {
  const renderMessage = (message) => {
    const { id, member, text } = message;
    const messageFromMe =
      member && member.clientData && member.clientData.username === currentMember.username;
    const className = messageFromMe
      ? "Messages-message currentMember"
      : "Messages-message";

    const avatarStyle = {
      backgroundColor: member.clientData.color,
    };

    const username = member?.clientData?.username ?? "Unknown User";

    return (
      <li className={className} key={id} >
        <span className="avatar" style={avatarStyle} />
        <div className="Message-content">
          <div className="username">{username}</div>
          <div className="text">{text}</div>
        </div>
      </li>
    );
  };

  return <ul className="Messages-list">{messages.map(renderMessage)}</ul>;
};

export default Messages;
