import React, { useState, useEffect } from 'react';
import './App.css';
import Messages from "./Messages";
import { randomColor, randomName } from './Random';
import Input from "./Input";

const App = () => {
  const [messages, setMessages] = useState([]);
  const [member, setMember] = useState({
    username: randomName(),
    color: randomColor()
  });

  useEffect(() => {
    const drone = new window.Scaledrone("MPfrKpGzAG9Ilhyp", {
      data: member
    });

    drone.on('open', (error) => {
      if (error) {
        return console.error(error);
      }
      const updatedMember = { ...member };
      updatedMember.id = drone.clientId;
      setMember(updatedMember);
    });

    const room = drone.subscribe("observable-room");

    room.on('data', (data, member) => {
      setMessages(prevMessages => [
        ...prevMessages,
        {
          text: data,
          member: member
        }
      ]);
    });

    // Cleanup function to disconnect the drone and unsubscribe from room when component unmounts
    return () => {
      drone.close();
      room.unsubscribe();
    };
  }, [member]); // Re-run effect whenever member changes

  const onSendMessage = (message) => {
    drone.publish({
      room: "observable-room",
      message
    });
  };

  return (
    <div className="App">
      <Messages
        messages={messages}
        currentMember={member}
      />
      <Input
        onSendMessage={onSendMessage}
      />
    </div>
  );
};

export default App;
