import React, { useState } from "react";
import handleTyping from './Messages'

const Input = ({ onSendMessage }) => {
const [text, setText] = useState("");

const onChange = (e) => {
setText(e.target.value);
};

const onSubmit = (e) => {
e.preventDefault();
setText("");
onSendMessage(text);
};

const Input = ({ onSendMessage }) => {
  const [message, setMessage] = useState('');

  const handleKeyDown = (event) => {
    if (event.key === 'Enter') {
      event.preventDefault();
      if (message.trim() !== '') {
        onSendMessage(message);
        setMessage('');
      }
    } else {
      handleTyping(true);
    }
  };

  const handleKeyUp = () => {
    handleTyping(false);
  };


return (
<div className="Input">
<form onSubmit={onSubmit}>
<input
       onChange={onChange}
       value={text}
       type="text"
       placeholder="Enter your message and press ENTER"
       autoFocus={true}

     />
<button>Send</button>
</form>
</div>
);
};
}
export default Input;
