import React, { useState, useEffect, useMemo } from 'react';
import './App.css';
import Messages from "./Messages";
import { randomColor, randomName } from './Random';
import Input from "./Input";

const App = () => {
  const [messages, setMessages] = useState([]);
  const [member, setMember] = useState({
    username: randomName(),
    color: randomColor()
  });

  //The 'drone' object construction makes the dependencies of useEffect Hook (at line 43) change on every render. To fix this, wrap the initialization of 'drone' in its own useMemo() Hook.
  const drone = useMemo(() => {
    return new window.Scaledrone("mKoCVA8scRizu5DS", {
      data: member
    });
  }, []);

  useEffect(() => {
    drone.on('open', (error) => {
      if (error) {
        return console.error(error);
      }
      const updatedMember = { ...member };
      updatedMember.id = drone.clientId;
      setMember(updatedMember);
    });

    const room = drone.subscribe("observable-room");

    room.on('data', (data, member) => {
      setMessages(prevMessages => [
        ...prevMessages,
        {
          text: data,
          member: member,
          timestamp: Date.now() 
        }
      ]);
    });

    // Cleanup function to unsubscribe from the room when component unmounts
    // return () => {
    //   room.unsubscribe();
    // };
  }, [drone, member]);

  const onSendMessage = (message) => {
    if (message.trim() !== "") {
      drone.publish({
        room: "observable-room",
        message,
      });
    }
  };

  return (
    <div className="App">
      <Messages
        messages={messages}
        currentMember={member}
      />
      <Input
        onSendMessage={onSendMessage}
      />
    </div>
  );
};

export default App;
